site.headStyle:
body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 0;
    background: linear-gradient(135deg, #6e8efb, #a777e3);
    color: white;
    line-height: 1.6;
}

.container {
    width: 80%;
    margin: auto;
    overflow: hidden;
}

header {
    background: rgba(0, 0, 0, 0.85);
    color: #ffffff;
    padding-top: 30px;
    min-height: 70px;
    border-bottom: #6e8efb 3px solid;
}

header a {
    color: #ffffff;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 16px;
}

header ul {
    padding: 0;
    list-style: none;
    text-align: center;
}

header li {
    display: inline;
    margin: 0 10px;
}

header .highlight, header .current a {
    color: #e9ff70;
    font-weight: bold;
}

.dropdown {
    position: relative;
    display: inline-block;
}

.dropdown-content {
    display: none;
    position: absolute;
    background-color: #f9f9f9;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
    z-index: 1;
    border-radius: 5px;
}

.dropdown-content a {
    color: black;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
    text-align: left;
}

.dropdown-content a:hover {
    background-color: #f1f1f1;
}

.dropdown:hover .dropdown-content {
    display: block;
}

section {
    padding: 60px 20px;
    text-align: center;
}

footer {
    background: #333333;
    color: #ffffff;
    text-align: center;
    padding: 20px 0;
}

/* Stile für die Spiele-Vorschau */
.game {
    margin-bottom: 20px;
}

.game-image {
    position: relative;
    height: 300px;
    background-size: cover;
    background-position: center;
    border-radius: 8px;
    overflow: hidden;
}

.game-info {
    background: rgba(0, 0, 0, 0.7);
    color: white;
    position: absolute;
    bottom: 0;
    padding: 20px;
    width: 100%;
}

.game-info h3 {
    margin: 0 0 10px 0;
}

.game-info p {
    margin: 0 0 15px 0;
}

.btn {
    display: inline-block;
    background: #6e8efb;
    color: white;
    padding: 10px 20px;
    text-decoration: none;
    border-radius: 5px;
    transition: background 0.3s;
}

.btn:hover {
    background: #a777e3;
}

/* Responsive Layout */
@media(max-width: 768px){
    .container {
        width: 95%;
    }

    header li, header .dropdown-content a {
        display: block;
        text-align: left;
    }

    .dropdown:hover .dropdown-content {
        display: block;
    }
}
nav ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: hidden;
}

nav ul li {
    float: left;
}

nav ul li a, .dropbtn {
    display: inline-block;
    color: white;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
}

nav ul li a:hover, .dropdown:hover .dropbtn {
    background-color: #575757;
}

/* Stil für das Dropdown-Menü */
.dropdown-content {
    display: none; /* Standardmäßig versteckt */
    position: absolute;
    background-color: #f9f9f9;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
    z-index: 1;
}

/* Stil für das Anzeigen des Dropdown-Menüs */
.dropdown:hover .dropdown-content {
    display: block;
}

site.headScript:
function toggleDropdown() {
    var dropdownContent = document.getElementById("dropdown-content");
    if (dropdownContent.style.display === "block") {
        dropdownContent.style.display = "none";
    } else {
        dropdownContent.style.display = "block";
    }
}
